userDefinedLinkHintCss:
/* Catpuccin Mocha Theme */

div > .vimiumHintMarker {
  /* linkhint boxes */
  background: #313244;  /* Surface0 */
  opacity: 0.7;
  border: none;
}

div > .vimiumHintMarker span {
  /* linkhint text */
  color: #a6adc8;   /* Subtext0 */
  opacity: 0.7;
  font-weight: bold;
  font-size: 12px;
}

div > .vimiumHintMarker > .matchingCharacter {
  opacity: 0.2
}
